import React from 'react';
import './About.css';

const About = () => {
  const skills = [
    'JavaScript', 'Python', 'React.js', 'HTML/CSS',
    'Machine Learning', 'Data Science', 'Docker',
    'MySQL', 'Firebase', 'Google Cloud', 'AWS', 'Bootstrap',
  ];

  return (
    <section id="about" className="section about">
      <p className="section-label">About</p>
      <h2 className="section-title">A bit about me</h2>

      <div className="about__grid">
        <div className="about__text">
          <p>
            I'm a Software Engineer at <strong>Amazon Ads</strong> based in Austin, Texas.
            I specialize in building scalable, reliable systems with a background 
            spanning software development, data science, and machine learning.
          </p>
          <p>
            Before Amazon, I worked at <strong>Nissan Motor Corporation</strong> and{' '}
            <strong>Tiger Analytics</strong>, building data pipelines and full-stack 
            applications. I hold a Master's in Computer Science from{' '}
            <strong>Arizona State University</strong>, where I was an Engineering 
            Graduate Scholarship recipient.
          </p>
        </div>

        <div className="about__skills">
          <h3 className="about__skills-title">Technologies</h3>
          <div className="about__skill-tags">
            {skills.map((skill) => (
              <span key={skill} className="about__tag">
                {skill}
              </span>
            ))}
          </div>
        </div>
      </div>
    </section>
  );
};

export default About;
