import React from 'react';
import './Awards.css';

const Awards = () => {
  const awards = [
    {
      title: 'Engineering Graduate Scholarship',
      org: 'Arizona State University',
      year: '2022',
    },
    {
      title: "Dean's Merit Scholarship",
      org: 'SASTRA University',
      year: '2019 — 2021',
    },
    {
      title: 'Indian Central Government Scholarship',
      org: 'Government of India',
      year: '2018 — 2022',
    },
    {
      title: 'Academic Excellence Award',
      org: 'SASTRA University',
      year: '2021',
    },
  ];

  return (
    <section id="awards" className="section awards">
      <p className="section-label">Awards</p>
      <h2 className="section-title">Recognition</h2>

      <div className="awards__list">
        {awards.map((award, i) => (
          <div key={i} className="awards__item">
            <div>
              <h3 className="awards__title">{award.title}</h3>
              <p className="awards__org">{award.org}</p>
            </div>
            <span className="awards__year">{award.year}</span>
          </div>
        ))}
      </div>
    </section>
  );
};

export default Awards;
