import React from 'react';
import './Hero.css';
import photo from '../me.jpeg';

const Hero = () => (
  <section id="home" className="hero">
    <div className="hero__wrap">
      <div className="hero__layout">
        <div className="hero__photo"><img src={photo} alt="Sreecharan Manne" /></div>
        <div className="hero__text">
          <h1 className="hero__name">Sreecharan Manne</h1>
          <p className="hero__role">Software Engineer at <strong>Amazon Ads</strong></p>
          <p className="hero__bio">Building scalable systems at the intersection of software engineering, machine learning, and cloud infrastructure.</p>
          <div className="hero__links">
            <a href="https://github.com/SreeCharanManne" target="_blank" rel="noopener noreferrer"><i className="fab fa-github" /> GitHub</a>
            <a href="https://linkedin.com/in/sree-charan-manne" target="_blank" rel="noopener noreferrer"><i className="fab fa-linkedin" /> LinkedIn</a>
            <a href="mailto:sreecharanmanne2000@gmail.com"><i className="fas fa-envelope" /> Email</a>
          </div>
        </div>
      </div>
    </div>
  </section>
);

export default Hero;
