import React from 'react';
import './Experience.css';

const Experience = () => {
  const experiences = [
    {
      title: 'Software Development Engineer',
      company: 'Amazon Ads',
      location: 'Austin, TX',
      period: 'Jun 2024 — Present',
      description: 'Building scalable advertising technology solutions.',
    },
    {
      title: 'Software Engineer',
      company: 'Nissan Motor Corporation',
      location: 'California',
      period: 'May 2023 — Mar 2024',
      description: 'Developed software systems for automotive engineering workflows.',
    },
    {
      title: 'Software Engineer',
      company: 'Tiger Analytics',
      location: 'Chennai, India',
      period: 'Aug 2021 — Jul 2022',
      description: 'Built data pipelines and full-stack analytics applications.',
    },
    {
      title: 'Software Intern',
      company: 'Tiger Analytics',
      location: 'Chennai, India',
      period: 'Feb 2021 — Jul 2021',
      description: 'Contributed to analytics platform development and data processing.',
    },
    {
      title: 'Software Engineer Intern',
      company: 'CodeMania',
      location: 'Hyderabad, India',
      period: 'Jan 2020 — Mar 2020',
      description: 'Worked on web application development and feature implementation.',
    },
  ];

  const education = [
    {
      degree: 'M.S. in Computer Science',
      school: 'Arizona State University',
      period: '2022 — 2024',
      note: 'Engineering Graduate Scholarship',
    },
    {
      degree: 'B.Tech in Computer Science',
      school: 'SASTRA University',
      period: '2017 — 2021',
      note: "Dean's Merit Scholarship",
    },
  ];

  return (
    <section id="experience" className="section experience">
      <p className="section-label">Experience</p>
      <h2 className="section-title">Where I've worked</h2>

      <div className="experience__list">
        {experiences.map((exp, i) => (
          <div key={i} className="experience__item">
            <div className="experience__header">
              <div>
                <h3 className="experience__role">{exp.title}</h3>
                <p className="experience__company">
                  {exp.company} <span className="experience__dot">·</span> {exp.location}
                </p>
              </div>
              <span className="experience__period">{exp.period}</span>
            </div>
            <p className="experience__desc">{exp.description}</p>
          </div>
        ))}
      </div>

      <div className="experience__education">
        <h3 className="experience__subtitle">Education</h3>
        {education.map((edu, i) => (
          <div key={i} className="experience__item">
            <div className="experience__header">
              <div>
                <h3 className="experience__role">{edu.degree}</h3>
                <p className="experience__company">{edu.school}</p>
              </div>
              <span className="experience__period">{edu.period}</span>
            </div>
            <p className="experience__desc">{edu.note}</p>
          </div>
        ))}
      </div>
    </section>
  );
};

export default Experience;
