import React from 'react';
import './Certifications.css';

const Certifications = () => {
  const certifications = [
    {
      name: 'Architecting with Google Compute Engine Specialization',
      issuer: 'Google Cloud Platform',
      icon: 'fab fa-google',
    },
    {
      name: 'AWS Cloud Practitioner',
      issuer: 'Amazon Web Services',
      icon: 'fab fa-aws',
    },
    {
      name: 'Microsoft Azure Fundamentals',
      issuer: 'Microsoft',
      icon: 'fab fa-microsoft',
    },
    {
      name: 'Tiger Edge — Student Developer Program in Data Science',
      issuer: 'Tiger Analytics',
      icon: 'fas fa-chart-bar',
    },
  ];

  return (
    <section id="certifications" className="section certifications">
      <p className="section-label">Certifications</p>
      <h2 className="section-title">Credentials</h2>

      <div className="certifications__list">
        {certifications.map((cert, i) => (
          <div key={i} className="certifications__item">
            <div className="certifications__icon">
              <i className={cert.icon} />
            </div>
            <div>
              <h3 className="certifications__name">{cert.name}</h3>
              <p className="certifications__issuer">{cert.issuer}</p>
            </div>
          </div>
        ))}
      </div>
    </section>
  );
};

export default Certifications;
