import React, { useState, useRef } from 'react';
import emailjs from '@emailjs/browser';
import { emailConfig as EMAIL_CONFIG } from '../emailConfig';
import './Contact.css';

const Contact = () => {
  const formRef = useRef();
  const [status, setStatus] = useState('');
  const [sending, setSending] = useState(false);

  const handleSubmit = (e) => {
    e.preventDefault();

    if (!EMAIL_CONFIG.serviceId || !EMAIL_CONFIG.templateId || !EMAIL_CONFIG.publicKey) {
      setStatus('error');
      return;
    }

    setSending(true);
    setStatus('');

    emailjs
      .sendForm(
        EMAIL_CONFIG.serviceId,
        EMAIL_CONFIG.templateId,
        formRef.current,
        EMAIL_CONFIG.publicKey
      )
      .then(() => {
        setStatus('success');
        formRef.current.reset();
        setSending(false);
      })
      .catch(() => {
        setStatus('error');
        setSending(false);
      });
  };

  return (
    <section id="contact" className="section contact">
      <p className="section-label">Contact</p>
      <h2 className="section-title">Get in touch</h2>

      <div className="contact__grid">
        <div className="contact__info">
          <p className="contact__text">
            I'm always open to new opportunities and conversations.
            Feel free to reach out.
          </p>
          <div className="contact__links">
            <a href="mailto:sreecharanmanne2000@gmail.com" className="contact__link-item">
              <i className="fas fa-envelope" />
              <span>sreecharanmanne2000@gmail.com</span>
            </a>
            <a
              href="https://linkedin.com/in/sree-charan-manne"
              target="_blank"
              rel="noopener noreferrer"
              className="contact__link-item"
            >
              <i className="fab fa-linkedin" />
              <span>LinkedIn</span>
            </a>
            <a
              href="https://github.com/SreeCharanManne"
              target="_blank"
              rel="noopener noreferrer"
              className="contact__link-item"
            >
              <i className="fab fa-github" />
              <span>GitHub</span>
            </a>
            <div className="contact__link-item">
              <i className="fas fa-map-marker-alt" />
              <span>Austin, Texas</span>
            </div>
          </div>
        </div>

        <form ref={formRef} onSubmit={handleSubmit} className="contact__form">
          <input
            type="text"
            name="from_name"
            placeholder="Name"
            required
            className="contact__input"
          />
          <input
            type="email"
            name="from_email"
            placeholder="Email"
            required
            className="contact__input"
          />
          <textarea
            name="message"
            placeholder="Message"
            rows="5"
            required
            className="contact__input contact__textarea"
          />
          <input type="hidden" name="to_name" value="Sreecharan" />

          <button type="submit" className="contact__button" disabled={sending}>
            {sending ? 'Sending...' : 'Send Message'}
          </button>

          {status === 'success' && (
            <p className="contact__status contact__status--success">
              Message sent successfully!
            </p>
          )}
          {status === 'error' && (
            <p className="contact__status contact__status--error">
              Something went wrong. Please try again.
            </p>
          )}
        </form>
      </div>
    </section>
  );
};

export default Contact;
